import React, { useState, useEffect } from 'react';
import { Container, Grid, Checkbox, Box, TextField, Card, CardContent, Typography, Button, adaptV4Theme } from '@mui/material';
import { ThemeProvider, StyledEngineProvider, createTheme, responsiveFontSizes } from '@mui/material/styles';
import validator from 'validator';
import './signup.css';
import { Route, Switch, Link } from 'react-router-dom';



// Theme for component
let theme = createTheme({
    palette: {
        primary: {
            main: '#00A36C',
        },

    },
    typography: {

        h5: {
            fontFamily: '"Poppins", sans-serif',
            color: '#ffffff',
            fontSize: '2.5rem',
            fontWeight: 'bold',
        },
        h6: {
            fontFamily: '"Poppins", sans-serif',
            fontWeight: 'bold',
            // color: '#ffffff',
            fontSize: '1.4rem'
        },
        h4: {
            fontFamily: '"Poppins", sans-serif',
            fontSize: '3rem',
            color: '#ffffff',
            fontWeight: 'bold',
        },
        h3: {
            fontFamily: '"Poppins", sans-serif',
            fontSize: '4rem',
            color: '#ffffff',
            fontWeight: 'bold',
        },
        overline: {
            fontWeight: 'bold',
            fontSize: '0.7rem',
            color: '#ffffff',
            fontWeight: 'bold',
        }
    }
});
theme = responsiveFontSizes(theme);

const Signup = () => {

    // useState for email
    const [email, setEmail] = useState('');

    // useState for password
    const [password, setPassword] = useState('');

    // useEffect to validate email 
    useEffect(() => {
        if (email === '') {
            document.getElementById('email').innerHTML = "&nbsp;s";
        } else if (validator.isEmail(email)) {
            document.getElementById('email').innerHTML = "&nbsp;";
        } else {
            document.getElementById('email').innerHTML = "Invalid email format !";
        }
    }, [email])

    // useEffect to handle signup function
    const handleSubmit = () => {

    }

    return (
        <StyledEngineProvider injectFirst>
            <ThemeProvider theme={theme}>
                <Box className='App'>
                    <Box className='app_2' >
                        <Grid container variant='column' >
                            <Grid item><article><Typography variant='h6' className='title' >Smart Question Bank</Typography></article>
                            </Grid>

                            <Grid item><Typography variant='h4' className='sub_title' > Smart Way to Create Questions by faculties.</Typography></Grid>
                            {/* <Grid item><img className={classes.img} src="study2.svg" height={200} width={200} alt="Image loading.."  /></Grid>  */}
                            <Grid><Box className='img_div'><img className='img' src="scene.svg" />   </Box>
                            </Grid>

                        </Grid>
                    </Box>

                    <Box className='app_3' >
                        <Box className='register'><Grid  >
                            <Typography variant='h6' >Register</Typography>
                            <br></br>
                            <Typography className='details' variant='h8' >Manage Your Account</Typography>
                            <br></br>
                            <br></br>
                            <Typography className='details_2' variant='h8' >Lets Get All You Set Up So You Can Verify Your Account</Typography>
                            <Box className='grid_box'>
                                <Grid container spacing={2} >
                                    <Grid item l={12} xs={24}><div className='name_box'><TextField variant='outlined' style={{ width: 260 }} size='small' label='First Name' type='name'  >FirstName</TextField></div></Grid>
                                    <Grid item l={12} xs={24}><TextField variant='outlined' size='small' style={{ width: 260 }} label='Last Name' type='name' >Last Name</TextField></Grid>
                                    <Grid item l={12} xs={24}><TextField variant='outlined' size='small' style={{ width: 260 }} label='Phone Number' type='phone' >Phone Number</TextField></Grid>
                                    <Grid item l={12} xs={24}><TextField variant='outlined' size='small' style={{ width: 260 }} label='Enter Email' type='email' >Email</TextField></Grid>
                                    <Grid item l={12} xs={24}><TextField variant='outlined' size='small' style={{ width: 260 }} label='Password' type='password' >Password</TextField></Grid>
                                    <Grid item l={12} xs={24}><TextField variant='outlined' size='small' style={{ width: 260 }} label=' Confirm Password' type='text' >Confirm Password</TextField></Grid>

                                </Grid>
                            </Box>
                        </Grid>
                        </Box>
                        <Box style={{ marginLeft: '10%' }}><Grid container><Checkbox className='check_1'></Checkbox>
                            <Box style={{ margin: 10 }} ><Typography className='details_2' variant='h7'>Yes I Want To Receive Email  </Typography></Box>

                        </Grid> </Box>
                        <Box className='checkbox'><Grid container><Checkbox></Checkbox>
                            <Box style={{ margin: 10 }} ><Typography style={{ fontStyle: 10 }} className='details_2' variant='h7'>Accept All Terms And Conditions</Typography></Box></Grid></Box>
                        <Button variant='contained' className='create_btn'>Create Account</Button>
                        <br></br>
                        <Box style={{ marginLeft: '10%', marginTop: 20 }}  >
                            <Grid container>
                                <Grid item><Typography variant='h5' style={{ fontSize: 15, marginRight: 5 }}>Already Have An Account?</Typography></Grid>
                                <Grid item><Link to='/loginscreen' style={{ textDecoration: 'none', }}><Typography className='details_5' variant='h5'  >Login</Typography></Link></Grid></Grid>
                        </Box>
                    </Box>





                </Box>
            </ThemeProvider>
        </StyledEngineProvider>
    );
}

export default Signup;
