import React,{useState} from 'react'
import {Typography,Box,TextField,Grid,Item,Button,Checkbox,Divider}from '@mui/material'
import {  Route,Switch,Link} from 'react-router-dom';
import App from '../App';
import makeStyles from '@mui/styles/makeStyles';
import { Media } from 'reactstrap';

const style = makeStyles({
app_2:{
    width:'70%',
    height:'100vh',
    position:'relative'
    //backgroundImage:`url(${process.env.PUBLIC_URL+ "/exam_paper2.jpg"})`,


},
grid:{

}
// div_box:{
//   marginTop:'15%'
// },
,
title:{
  fontWeight:'bold'
},
sub_title:{
},
sub_title2:{
  color:'#898484',
    fontSize:15,

},
sub_title3:{
  color:'#898484',
    fontSize:15,
    marginBottom:'10px',
    marginTop:'10px'

},
sub_title4:{
  color:'#5463FF',
    fontSize:15,
    marginBottom:'10px',
    marginTop:'10px',
    marginLeft:'10px',
    fontWeight:'bold'

},
btn_login:{
  width:300,
  marginTop:'20px',
  background:'#5463FF',
  color:'white',
  '&:hover': {
    backgroundColor: '#5463FF',
    boxShadow: 'none',
    color:'#fff'
  }
},
img_box:{
  flex:'cover',
  height:'100vh',
  maxWidth:'100%',
  maxHeight:'100%',
  mixBlendMode:'darken',
  filter:'brightness(70%)'


},
div_box:{
  marginTop:'40px'
},
c1:{
  marginBottom:'20px',
  marginTop:'20px'
},
des:{
  position:'absolute',
  bottom:'20px',
  width:'90vh',
  left:'20px'
},
padding_box:{
  paddingRight:'40px',
  paddingLeft:'20px'
}
,
app_3:{
    width:'30%',
    backgroundColor:'#FAF3F3',
    justifyContent:'center',
    padding:'5%'


}
})

function LoginScreen() {
    const classes = style();
    const[username,setUsername] = useState('');
    const[password,setPassword] = useState('');
    const handle_submit = (e)=>{
      e.preventDefault();
      if(username && password){
        console.log(username,password);

      }

    }


  return (

    <Switch>

      <Box className='App'>
        <Box className={classes.app_2}>





<img className={classes.img_box} src='exam_logo.jpg' />
<Box  className={classes.des} ><Grid><Typography variant='h4'  style={{color:'#FAF3F3',fontWeight:'bold'}}>Easiest Way To Create Questions ,
faculty friendly</Typography></Grid></Box>

        </Box>
        <Box className={classes.app_3}>
         <Box className={classes.padding_box}> <Typography className={classes.title} variant='h5'>LogIn</Typography>
          <Box className={classes.div_box} >
          <Box className={classes.c1} ><Typography className={classes.sub_title} variant = 'h6'>Login To Your Account</Typography></Box>

            <Box className={classes.c1} ><Typography className={classes.sub_title2} variant = 'h6'>Thank You For Getting Back With Us,Lets start exploring the Questions</Typography>

            <Box style={{marginTop:10,marginBottom:10}} ><Divider light></Divider></Box>

            </Box>
            </Box>
            <Grid xs={12} className={classes.grid}>
            <form noValidate autoComplete='off'  >
              <Grid xs={12}>
                <TextField variant='outlined' required onChange={(e)=>(setUsername(e.target.value))} size='small'style={{width:300,marginBottom:20,marginTop:10}} label='User Name'  type='name' ></TextField></Grid>
            <Grid xs={12}>
              <TextField variant='outlined' required onChange={(e)=>(setPassword(e.target.value))} size='small'  style={{width:300}} label='Password' type='password' ></TextField></Grid>
            <Grid xs={12}>
              <Button className={classes.btn_login} onClick={handle_submit} ><Typography  variant='h7'>Login</Typography></Button></Grid>
          </form>
            </Grid>
            <Grid container>
            <Grid item><Typography variant='h6' className={classes.sub_title3}>Haven't Registered Yet?</Typography></Grid>
            <Grid item><Link to='/' style={{textDecoration:'none'}}><Typography className={classes.sub_title4}>  Register Now</Typography></Link></Grid>

            </Grid>
          </Box>

          </Box>
    </Box>



    </Switch>

  )
}

export default LoginScreen
